--
-- Table structure for table `tbl_users`
--

CREATE TABLE IF NOT EXISTS `tbl_users` (
  `user_id` int(5) NOT NULL AUTO_INCREMENT,
  `user_name` varchar(50) NOT NULL,
  `user_first_name` varchar(100) NOT NULL,
  `user_last_name` varchar(50) NOT NULL,
  `user_email_address` varchar(100) NOT NULL,
  `user_password` varchar(50) NOT NULL,
  `user_gender` tinyint(1) NOT NULL,
  `user_date_of_birth` datetime NOT NULL,
  `user_country` varchar(50) NOT NULL,
  `user_mailing_address` varchar(255) NOT NULL,
  `user_category` varchar(50) NOT NULL DEFAULT 'user',
  `user_profile_image` varchar(255) NOT NULL,
  `user_create_date_and_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `user_status` tinyint(1) NOT NULL DEFAULT '1',
  PRIMARY KEY (`user_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=13 ;

